import com.wuweibi.module4j.ModuleActivatorimport com.wuweibi.module4j.SupperModuleimport com.wuweibi.module4j.module.ModuleContextimport java.lang.Classimport groovy.lang.GroovyObject;import org.marker.mushroom.ext.model.ContentModel;import org.marker.mushroom.ext.model.ContentModelContext;/** * 栏目模块  *  * @author marker */public class ModuleActivatorImpl   extends SupperModule {		 	/**	 * 启用 	 */	public void start(ModuleContext context) throws Exception {		GroovyObject cm = util.loadModelObject("model");		ContentModelContext.getInstance().put(cm);	} 			/**	 * 停用	 */	public void stop(ModuleContext context) throws Exception {		ContentModelContext.getInstance().remove("channel"); 	}	}